        // b%(1) bm(30) (1) showing 1, 15, 1
        $activeDieArrayArray = $game->activeDieArrayArray;
        $this->assertEquals(1, $activeDieArrayArray[0][0]->value);
        $this->assertEquals(1, $activeDieArrayArray[0][2]->value);
