        $this->assertEquals(1, $activeDieArrayArray[1][2]->value);
        $activeDieArrayArray[0][1]->value = 18;
        $activeDieArrayArray[1][1]->value = 15;

        // check that boom attacks do not impart null
        // results in:
        // bv(30) b(1) (1) showing 18, 1, 1
